mapper.select:
select id,address,title,start_time,end_time
from activite
where id = #{activityId}

mapper.update:
update message_subscribe
set is_open=NOT is_open
where user_id=#{userId}

mapper.delete:
delete from message_clock
where user_id=#{userId}
and activity_id=#{activityId}

mapper.update:
update message_clock
set clock_id=#{clockId}
where user_id=#{userId}
and activity_id=#{activityId}
